import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    formData:{
        name:'',
        email:'',
        address:'',
        cardNumber:'',
        expiration:'',
        cvv:''
    },
    errors:{}
}

const checkoutFormSlice = {
    name:"checkoutFormSlice",
    initialState,
    reducers:{
        updateFormData(state, action) {
            const { name, value } = action.payload;
            state.formData[name] = value;
          },
    }

}
