import logo from './logo.svg';
import './App.css';
import { Container, Row, Col, Button, Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState, useEffect } from 'react';
function App() {
  const [categories, setCategories] = useState([])
  const [categoryproducts, setCategoryproducts] = useState([])
  const getAllCategories = ()=>{
    return fetch('https://fakestoreapi.com/products/categories')
            .then(res=>res.json())
            .then((cat)=>setCategories(cat))


  }

  useEffect(() => {
    getAllCategories();
  }, [])

  const getCategoryProducts = async (category)=>{
    let catname = category
    const catProducts =  fetch('https://fakestoreapi.com/products/category/'+catname.category)
    .then(res=>res.json())
    .then((catproduct)=>setCategoryproducts(catproduct))

    return catProducts
  }
  // useEffect(() => {
  //   getCategoryProducts();
  // }, [])


  return (
    <>
    <div className="App">
      <h1 style={{ color: "green" }}>Demo store</h1>
      <center>
        {categories.map((category) => {
          return (
            <>
             <div
              style={{
                width: "15em",
                backgroundColor: "#35D841",
                padding: 2,
                borderRadius: 10,
                marginBlock: 10,
                float:'left',
                marginLeft:10


              }}
            >
              <p style={{ fontSize: 20, color: 'white',cursor:'pointer' }} onClick={()=>getCategoryProducts({category})}>{category}</p>

            </div>
            <div className='categoryproducts'>
                {categoryproducts.map((catproduct)=>{
                  console.log('catproduct',catproduct)
                  return(<>{catproduct.title}</>)
                })}
              </div>
            </>

          );
        })}
      </center>
    </div> 
    <Container className="text-center my-5">
			<Card bg="info" text="white">
				<Card.Body>
					<Card.Title>Welcome to our Website</Card.Title>
					<Card.Text>
						Discover the beauty of React Bootstrap Layout with a stunning container component approach.
					</Card.Text>
					<Button variant="light">Learn More</Button>
				</Card.Body>
			</Card>
		</Container>   

    </>

  );
}

export default App;
